vertexShader:
precision highp float;
precision highp int;
attribute vec3 a_position;
attribute vec2 a_uv;
uniform mat4 u_mvp;
varying vec2 v_uv;
void main()
{
  gl_Position = u_mvp * vec4(a_position,1.0);
  v_uv = a_uv;
}

fragmentShader:
precision mediump float;
precision mediump int;
uniform sampler2D s_diffuse;
varying vec2 v_uv;
void main()
{
  gl_FragColor = texture2D(s_diffuse, v_uv);
}
